function getInfo(){fetch("https://swapi.co/api/films/").then(function(e){e.json().then(function(e){appendUsersToSection(e.results)})})["catch"](function(){updateInfoWithError()})}function appendUsersToSection(e){const t=document.getElementById("filmlist");e.forEach(function(e){formatUserDiv(e).then(function(e){let n=document.createElement("div"),o=e;n.innerHTML=o,t.appendChild(n)})})}function formatUserDiv(e){return new Promise(function(t){let n=`<h2> ${e.title}</h2>`;n+=`<p>Movie director: ${e.director}</p>`,n+=`<p>Episode: ${e.episode_id}</p>`,n+=`<p>Release date: ${e.release_date}</p>`,console.log(n),t(n)})}document.addEventListener("DOMContentLoaded",function(){getInfo()});