function getInfo(){fetch("https://swapi.co/api/people/").then(function(n){n.json().then(function(n){appendUsersToSection(n.results)})})["catch"](function(){updateInfoWithError()})}function appendUsersToSection(n){const e=document.getElementById("peoplelist");n.forEach(function(n){formatUserDiv(n).then(function(n){let t=document.createElement("div"),o=n;t.innerHTML=o,e.appendChild(t)})})}function formatUserDiv(n){return new Promise(function(e){getHomeworld(n.homeworld).then(function(t){let o=`<h2> ${n.name}</h2>`;o+=`<p>Height: ${n.height}</p>`,o+=`<p>Mass: ${n.mass}</p>`,o+=`<p>Eye Color: ${n.eye_color}</p>`,o+=`<p>Homeworld: ${t}</p>`,console.log(o),e(o)})})}function getHomeworld(n){return new Promise(function(e){fetch(n).then(function(n){n.json().then(function(n){e(n.name)})})})}document.addEventListener("DOMContentLoaded",function(){getInfo()});