function getInfo(){fetch("https://swapi.co/api/planets/").then(function(n){n.json().then(function(n){appendUsersToSection(n.results)})})["catch"](function(){updateInfoWithError()})}function appendUsersToSection(n){const t=document.getElementById("planetlist");n.forEach(function(n){formatUserDiv(n).then(function(n){let e=document.createElement("div"),o=n;e.innerHTML=o,t.appendChild(e)})})}function formatUserDiv(n){return new Promise(function(t){let e=`<h2> ${n.name}</h2>`;e+=`<p>Climate: ${n.climate}</p>`,e+=`<p>Rotation period: ${n.rotation_period}</p>`,e+=`<p>Population: ${n.population}</p>`,e+=`<p>Terrain: ${n.terrain}</p>`,e+=`<p>Diameter: ${n.diameter}</p>`,console.log(e),t(e)})}document.addEventListener("DOMContentLoaded",function(){getInfo()});